import{_ as o,r as n,o as h,c as d,a,b as s,w as r,d as e,e as t}from"./app.6043c0e4.js";const l={},c=t('<h1 id="\u5173\u4E8E-\u539F\u5B50\u6027\u77E5\u8BC6\u70B9" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E-\u539F\u5B50\u6027\u77E5\u8BC6\u70B9" aria-hidden="true">#</a> \u5173\u4E8E \u539F\u5B50\u6027\u77E5\u8BC6\u70B9</h1><p><strong>\u539F\u5B50\u6027\u77E5\u8BC6\u70B9(Atomicity knowledge points)</strong>,\u4EE5\u4E0B\u7B80\u79F0<strong>\u539F\u5B50\u77E5\u8BC6(atomic knowledge)</strong>,\u662F\u7531Ocean \u56E2\u961F\u5185\u90E8\u7ECF\u8FC7\u591A\u5E74\u7684\u5B66\u4E60\u4EE5\u53CA\u9A8C\u8BC1\uFF0C\u53D1\u73B0\u77E5\u8BC6\u7684\u6784\u6210\u53EF\u4EE5\u63CF\u8FF0\u4E3A\uFF1A\u662F\u7531\u4F17\u591A\u539F\u5B50\u77E5\u8BC6\u8FDB\u884C\u7EC4\u5408\u800C\u6210\u7684(\u8FD9\u91CC\u7684\u539F\u5B50\u501F\u7528\u4E86\u7269\u7406\u5B66\u548C\u5316\u5B66\u4E2D\u539F\u5B50\u7684\u6982\u5FF5\u5BF9\u77E5\u8BC6\u8FDB\u884C\u63CF\u8FF0)\uFF0C\u5E76\u4EE5\u8FD9\u4E2A\u53D1\u73B0\u4E3A\u57FA\u7840,\u63D0\u51FA\u7684\u4E00\u79CD\u77E5\u8BC6\u70B9\u7684\u7C7B\u578B\u3002</p><div class="custom-container tip"><i class="fas fa-info"></i><p>\u539F\u5B50\uFF1A\u6307\u5316\u5B66\u53CD\u5E94\u4E0D\u53EF\u518D\u5206\u7684\u57FA\u672C\u5FAE\u7C92\u3002\u5E76\u4E14\u662F\u7EC4\u6210\u7269\u4F53\u7684\u57FA\u672C\u7C92\u5B50\u3002</p></div><p>\u4ECE\u7269\u7406\u5B66\u4EE5\u53CA\u5316\u5B66\u7684\u77E5\u8BC6\u8FC1\u79FB\uFF0C\u6211\u4EEC\u77E5\u9053\uFF1A\u4E00\u65E6\u4E00\u4E2A\u4E8B\u7269\u88AB\u63CF\u8FF0\u5177\u6709\u539F\u5B50\u6027\uFF0C\u90A3\u5B83\u4E00\u5B9A\u5177\u6709\u4E00\u4E0B\u51E0\u79CD\u7279\u6027\uFF1A\u4E0D\u53EF\u518D\u5206\u6027,\u57FA\u7840\u6027,\u53EF\u8FC1\u79FB\u6027,\u53EF\u7EC4\u5408\u6027\u3002\u800C\u539F\u5B50\u4E0E\u77E5\u8BC6\u7ED3\u5408\u540E\u7684\u539F\u5B50\u77E5\u8BC6\uFF0C\u4E00\u5B9A\u8FD8\u6709\u4E00\u4E2A\u7279\u6027\u5C31\u662F\u79C1\u4EBA\u6027\u3002</p><h2 id="\u4E0D\u53EF\u518D\u5206\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u53EF\u518D\u5206\u6027" aria-hidden="true">#</a> \u4E0D\u53EF\u518D\u5206\u6027</h2><p>\u662F\u6307\u5355\u72EC\u7684\u77E5\u8BC6\u70B9\u4E0D\u53EF\u4EE5\u518D\u6B21\u5206\u5272\uFF0C\u662F\u5C5E\u4E8E<strong>\u4E2A\u4EBA</strong>\u7684<strong>\u6700\u5C0F\u7684\u77E5\u8BC6\u70B9</strong>\uFF0C\u4F46\u4E5F\u4F1A\u56E0\u4EBA\u800C\u5F02\uFF0C\u8FD9\u70B9\u5728\u79C1\u4EBA\u6027\u54EA\u91CC\u6709\u6240\u4F53\u73B0\u3002</p><h2 id="\u57FA\u7840\u6027" tabindex="-1"><a class="header-anchor" href="#\u57FA\u7840\u6027" aria-hidden="true">#</a> \u57FA\u7840\u6027</h2><p>\u662F\u6307\u5BF9\u4E8E\u8FD9\u4E2A\u77E5\u8BC6\u70B9\u662F\u5C5E\u4E8E\u4E2A\u4EBA\u7684\u6700\u57FA\u7840\u7684\u77E5\u8BC6\uFF0C\u4EE5\u6B64\u4E3A\u57FA\u7840\u53EF\u4EE5\u7528\u6765 \u5206\u6790\u3001\u89E3\u91CA\u3001\u63A8\u7406\u3001\u6F14\u5316 \u5176\u4ED6\u7684\u77E5\u8BC6\u70B9\u3002</p><h2 id="\u53EF\u8FC1\u79FB\u6027" tabindex="-1"><a class="header-anchor" href="#\u53EF\u8FC1\u79FB\u6027" aria-hidden="true">#</a> \u53EF\u8FC1\u79FB\u6027</h2><p>\u662F\u6307\u5BF9\u4E8E\u67D0\u4E2A\u77E5\u8BC6\u70B9\u53EF\u4EE5\u65E0\u7F1D\u8FC1\u79FB\u5230\u5176\u4ED6\u7684\u77E5\u8BC6\u70B9\u4E2D\u3002</p><p>\u6BD4\u5982\u53D8\u91CF\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u5728\u6570\u5B66\u91CC\u662F\u6307\u51FD\u6570\u4E2D\u7684\u4F8B\u5982\uFF1Ay=ax+b\uFF0C\u5176\u4E2Da\u4E0Eb\u662F\u5E38\u6570\uFF0C\u4E2Dx\u4E3A\u81EA\u53D8\u91CF\uFF0C\u8868\u793Ax\u968F\u7740\u5404\u79CD\u5404\u6837\u7684\u539F\u56E0\u6570\u503C\u8FDB\u884C\u81EA\u6211\u53D8\u5316\uFF0C\u6240\u4EE5\u79F0\u4E3A\u53D8\u91CF\uFF0C\u53C8\u56E0\u4E3A<strong>\u81EA\u6211\u53D8\u5316</strong>\u6240\u4EE5\u6210\u4E3A\u81EA\u53D8\u91CF\u3002\u800C\u4EE3\u7801\u4E2D\u4E5F\u6709\u53D8\u91CF\u7684\u6982\u5FF5\uFF0C\u672C\u8D28\u4E0A\u4E0E\u81EA\u53D8\u91CF\u65E0\u5DEE\u522B\uFF0C\u4E3B\u8981\u63CF\u8FF0\uFF1A\u91CC\u9762\u7684\u503C\u662F\u53EF\u4EE5\u7ECF\u8FC7\u5404\u79CD\u5404\u6837\u8FDB\u884C\u53D8\u5316\uFF0C\u6240\u4EE5\u53D8\u91CF\u8FD9\u4E2A\u77E5\u8BC6\u70B9\u5177\u6709\u8FC1\u79FB\u6027\u3002</p><h2 id="\u53EF\u7EC4\u5408\u6027" tabindex="-1"><a class="header-anchor" href="#\u53EF\u7EC4\u5408\u6027" aria-hidden="true">#</a> \u53EF\u7EC4\u5408\u6027</h2><p>\u662F\u6307\u67D0\u4E2A\u77E5\u8BC6\u70B9\u4E0E\u5176\u4ED6\u7684\u77E5\u8BC6\u70B9\u53EF\u4EE5\u8FDB\u884C\u7EC4\u6210\u5F62\u6210\u65B0\u7684\u77E5\u8BC6\u70B9\u3002</p><p>\u4F8B\u5982\uFF1A\u6570\u5B66\u4E2D\u7684\u52A0\u6CD5\u7B49\u5F0F\uFF0C\u53EF\u4EE5\u7531\u6570\u5B57(\u5177\u4F53\u6765\u8BF4\u662F\u81EA\u7136\u6570)\uFF0C\u6570\u5B66\u52A0\u6CD5\uFF0C\u4EE5\u53CA\u4EF7\u503C\u76F8\u7B49\u7EC4\u6210\u3002\u800C1+1=2\u5C31\u662F\u8FD9\u79CD\u6CD5\u5219\u7684\u5177\u4F53\u8868\u73B0\u3002</p><div class="custom-container tip"><i class="fas fa-info"></i><p>1+1=2\uFF0C\u8FD9\u4E2A\u4F8B\u5B50\u867D\u7136\u7B80\u5355\uFF0C\u4F46\u5B83\u662F\u9884\u793A\u7740\u52A0\u6CD5\u7684\u81EA\u7136\u6CD5\u5219\u3002\u800C\u5728\u8FD9\u91CC\u6211\u4EEC\u9700\u8981\u8FDB\u884C\u62C6\u89E3\u5B83\uFF1A</p><p>\u2235 1\u30011\u30012\uFF0C\u5206\u522B\u662F\u4E09\u4E2A\u81EA\u7136\u6570\u3002\u81EA\u7136\u6570\u7684\u6982\u5FF5\uFF1A\u81EA\u589E\u7684\u6570\u5217\uFF0C\u540C\u65F6\u4E4B\u95F4\u95F4\u9694\u4E3A1\u3002</p><p>\u2235 +\uFF1A\u8FD9\u91CC\u662F\u6570\u5B66\u4E2D\u6240\u5B9A\u4E49\u7684\u4E00\u4E2A\u8868\u793A\u6574\u4F53\u7EDF\u8BA1\u7684\uFF0C\u5982\uFF1A1+1\u5219\u8868\u793A1\u4E2A\u5355\u4F4D\u4E0E\u53E61\u5355\u4F4D\u7684\uFF0C\u5168\u90E8\u6574\u4F53\u7684\u6570\u8FDB\u884C\u7EDF\u8BA1\u3002</p><p>\u2235 =\uFF1A\u8FD9\u91CC\u6307\u7684\u662F\u67D0\u4E2A\u4E1C\u897F\u4E0E\u53E6\u4E00\u4E2A\u4E1C\u897F\u5728\u67D0\u65B9\u9762\u662F\u76F8\u540C\u7684\u3002</p><p>\u53C8\u2235 \u6570\u5B66\u4E3B\u8981\u8BA8\u8BBA\u6570\u91CF\u4E0A\u7684\u53D8\u5316\u3002</p><p>\u2234 \u800C\u7EFC\u4E0A\u6240\u8FF0\uFF1A1+1=2\u8868\u793A\u77401\u8FD9\u4E2A\u81EA\u7136\u6570\u4E0E1\u8FD9\u4E2A\u81EA\u7136\u6570\u8FDB\u884C\u6574\u4F53\u7EDF\u8BA1\u800C\u8A00\u4E0E2\u8FD9\u4E2A\u81EA\u7136\u6570\u5728\u6570\u91CF\u4E0A\u662F\u76F8\u540C\u7684\u3002</p><p>Q.E.D.</p></div><p>\u4E0A\u9762\u7684\u4F8B\u5B50\uFF0C\u5047\u8BBE\u5728 \u81EA\u7136\u6570\uFF0C\u6570\u5B66\u52A0\u6CD5\uFF0C\u4EF7\u503C\u76F8\u7B49 \u8FD9\u4E09\u4E2A\u6982\u5FF5\u4E0D\u5728\u7EE7\u7EED\u5F80\u4E0B\u5206\u7684\u65F6\u5019\u3002\u8FD9\u4E09\u4E2A\u5BF9\u4E8E\u6570\u5B66\u800C\u8A00\u5C31\u662F\u539F\u5B50\u77E5\u8BC6\u3002\u5E76\u4E14\u4ED6\u4EEC\u8FDB\u884C\u7EC4\u6210\u5C31\u80FD\u5F62\u6210\u52A0\u6CD5\u7B49\u5F0F\u8FD9\u4E2A\u77E5\u8BC6\u70B9\u3002\u8868\u660E\u4E86\u539F\u5B50\u77E5\u8BC6\u7684\u7EC4\u5408\u6027\u3002</p><h2 id="\u79C1\u4EBA\u6027" tabindex="-1"><a class="header-anchor" href="#\u79C1\u4EBA\u6027" aria-hidden="true">#</a> \u79C1\u4EBA\u6027</h2><p>\u8FD9\u70B9\u662F\u4ECE\u7528\u6237\u8005\u7684\u89D2\u5EA6\u51FA\u53D1\u7684\u3002\u56E0\u4E3A\u6BCF\u4E2A\u4EBA\u5BF9\u4E8E\u4E16\u754C\u7684\u7406\u89E3\u4E0D\u540C\uFF0C\u6240\u638C\u63E1\u7684\u57FA\u7840\u4E0D\u540C\uFF0C\u6240\u63A5\u53D7\u7684\u6559\u80B2\u4E0D\u540C\uFF0C\u7ECF\u5386\u4E0D\u540C\u3002\u6240\u4EE5\u6BCF\u4E2A\u4EBA\u7684\u539F\u5B50\u77E5\u8BC6\u7684\u91CF\u4E0D\u540C\uFF0C\u539F\u5B50\u77E5\u8BC6\u7684\u7EC6\u5EA6\u4E5F\u4E0D\u76F8\u540C\u3002</p><p>\u5728 <strong>\u53EF\u7EC4\u5408\u6027</strong> \u7684\u4F8B\u5B50\u4E2D\u51FA\u73B0\u7684\u4F8B\u5B50\uFF0C\u91CC\u9762\u6240\u8FF0\u7684\u5982\u679C\u8FD9\u4E09\u4E2A\u6982\u5FF5\u4E0D\u5728\u7EE7\u7EED\u5F80\u4E0B\u5206\u7684\u65F6\u5019\u3002\u5C31\u8868\u660E\u4E86\u5176\u5B9E\u6309\u7167\u5B9E\u9645\u800C\u8A00\u5B83\u53EF\u4EE5\u7EE7\u7EED\u5F80\u4E0B\u8FDB\u884C\u5206\u6790\u3002\u6BD4\u5982\u81EA\u7136\u6570\u8FD9\u4E2A\u77E5\u8BC6\u8FD8\u53EF\u4EE5\u8FDB\u884C\u5F80\u4E0B\u7EC6\u5206\u4E3A\u6570\u5B57\uFF0C\u5982\u679C\u518D\u5F80\u4E0B\u5206\uFF0C\u81EA\u7136\u6570\u5C31\u4E0D\u662F\u4E00\u4E2A\u539F\u5B50\u77E5\u8BC6\uFF0C\u800C\u662F\u4E00\u4E2A <strong>\u5206\u5B50\u77E5\u8BC6</strong> \u3002</p><h3 id="\u5206\u5B50\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u5206\u5B50\u77E5\u8BC6" aria-hidden="true">#</a> \u5206\u5B50\u77E5\u8BC6</h3><p>\u5206\u5B50\u77E5\u8BC6\uFF1A\u7531\u539F\u5B50\u77E5\u8BC6\u6216\u5206\u5B50\u77E5\u8BC6\u8FDB\u884C\u7EC4\u5408\u800C\u6210\u7684\u3002</p><p>\u6700\u57FA\u672C\u7684\u539F\u5B50\u77E5\u8BC6\u4E4B\u95F4\u76F8\u4E92\u7EC4\u6210\u6700\u57FA\u672C\u7684\u5206\u5B50\u77E5\u8BC6\uFF0C\u800C\u5206\u5B50\u77E5\u8BC6\u53C8\u4E92\u76F8\u7EC4\u6210\u65B0\u7684\u5206\u5B50\u77E5\u8BC6\u3002\u4E5F\u53EF\u4EE5\u662F\u5206\u5B50\u77E5\u8BC6\u4E0E\u539F\u5B50\u77E5\u8BC6\u7EC4\u6210\u65B0\u7684\u5206\u5B50\u77E5\u8BC6\u3002\u8FD9\u662F\u5206\u5B50\u77E5\u8BC6\u7684\u6784\u6210\u3002</p><p>\u800C\u6211\u4EEC\u4E00\u822C\u6240\u9047\u5230\u7684\u67D0\u4E2A\u9886\u57DF\u7684\u77E5\u8BC6\u90FD\u662F\u5206\u5B50\u77E5\u8BC6\u3002\u800C\u5206\u5B50\u77E5\u8BC6\u4E2D\u4E00\u5B9A\u5305\u542B\u67D0\u4E2A\u5728\u662F\u6211\u4EEC\u6CA1\u6709\u89C1\u8FC7\u7684\u5206\u5B50\u77E5\u8BC6\uFF0C\u800C\u8FD9\u4E2A\u65F6\u5019\u6B63\u786E\u7684\u505A\u6CD5\u662F\u5C06\u8FD9\u4E2A\u5206\u5B50\u77E5\u8BC6\u641E\u61C2\u662F\u4EC0\u4E48\uFF0C\u4ECE\u800C\u8BA9\u539F\u672C\u7684\u77E5\u8BC6\u70B9\u4E0D\u4F1A\u51FA\u73B0\u7406\u89E3\u4E0A\u7684\u56F0\u96BE\u3002\u5982\u679C\u5728\u8FD9\u4E2A\u57FA\u7840\u5206\u5B50\u77E5\u8BC6\u7684\u65F6\u5019\u540C\u6837\u9047\u5230\u6CA1\u6709\u89C1\u8FC7\u7684\u5206\u5B50\u77E5\u8BC6\uFF0C\u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u5C31\u4E0D\u65AD\u518D\u5206\u3002</p><p>\u4F8B\uFF1A\u6211\u4EEC\u5728\u5B66\u4E60A\u77E5\u8BC6\u7684\u65F6\u5019\uFF0CA\u53EF\u80FD\u7531B\u77E5\u8BC6\u548CC\u77E5\u8BC6\u7EC4\u6210=\u300BA=B+C</p><p>\u5047\u8BBEB\u77E5\u8BC6\u8FD9\u4E2A\u5206\u5B50\u6216\u8005\u539F\u5B50\u77E5\u8BC6\u70B9\u6211\u4EEC\u6240\u719F\u77E5\u3002\u800CC\u8FD9\u4E2A\u5206\u5B50\u6216\u539F\u5B50\u77E5\u8BC6\u70B9\u6211\u4EEC\u4E0D\u719F\u6089\u3002 \u8FD9\u4E2A\u65F6\u5019\u6211\u4EEC\u9700\u8981\u7EE7\u7EED\u7814\u7A76C\u77E5\u8BC6\u70B9\uFF0C\u4EE5\u4FDD\u8BC1A\u77E5\u8BC6\u70B9\u7684\u7406\u89E3\u3002</p><p>\u4F46\u5F88\u6709\u53EF\u80FDC\u77E5\u8BC6\u70B9\u662F\u4E2A\u5206\u5B50\u77E5\u8BC6\u70B9\uFF0C\u5B83\u662F\u7531D\u77E5\u8BC6\u4E0EE\u77E5\u8BC6\u8FDB\u884C\u7EC4\u6210\u7684=\u300BC=D+E \u8FD9\u4E2A\u65F6\u5019\u5728\u7814\u7A76C\u77E5\u8BC6\u70B9\u7684\u65F6\u5019\u53D1\u73B0E\u77E5\u8BC6\u7684\u65F6\u5019\u53D1\u73B0\u662F\u6211\u4EEC\u6240\u719F\u77E5\u7684\uFF0C\u800CD\u77E5\u8BC6\u662F\u6211\u4EEC\u6240\u4E0D\u719F\u77E5\u7684\u3002</p><p>\u6B64\u65F6\u6211\u4EEC\u8FD8\u9700\u8981\u7814\u7A76D\u77E5\u8BC6\u70B9\u3002</p><p>\u6240\u4EE5\u7EFC\u4E0A\u6240\u8FF0A=B+(D+E).</p><p>\u8FD9\u6837\u9010\u5C42\u5206\u5C42\u76F4\u5230\u5206\u89E3\u5230\u90FD\u662F\u719F\u77E5\u7684\u77E5\u8BC6\u70B9\u65F6\u5019\uFF0C\u5C31\u6BD4\u5982\u73B0\u5728B\u3001D\u3001E\u90FD\u662F\u6211\u4EEC\u6240\u719F\u77E5\u7684\u77E5\u8BC6\u70B9\u65F6\uFF0CB\u3001D\u3001E\u5BF9\u4E8E\u6211\u4EEC\u800C\u8A00\u5C31\u662F\u539F\u5B50\u77E5\u8BC6\u70B9\u3002</p><p>\u6CE8\uFF1A\u6B64\u65F6\u8981\u6CE8\u610F\uFF0C\u53EF\u80FDB\u3001D\u3001E\u53EF\u80FD\u8FD8\u53EF\u4EE5\u518D\u5206\u3002\u4F46\u5BF9\u4E8E\u4E2A\u4EBA\u800C\u8A00\u6765\u8BF4\u662F\u5DF2\u7ECF\u5230\u4E86\u539F\u5B50\u5C42\u9762\uFF0C\u8FD9\u91CCOceanT\u56E2\u961F\u8FD8\u662F\u63A8\u8350\u5927\u5BB6\u591A\u7684\u5F80\u4E0B\u7EC6\u5206\u77E5\u8BC6\u70B9\uFF0C\u56E0\u4E3A\u8BF4\u4E0D\u5B9A <strong>E</strong> \u91CC\u9762\u7684 <strong>F</strong> \u5C31\u662F\u7EC4\u6210\u5176\u4ED6\u77E5\u8BC6 <strong>\uFF08\u6709\u522B\u4E8EA\uFF09</strong> \u7684\u539F\u5B50\u77E5\u8BC6\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p>\u8FD9\u91CC\u91CD\u70B9\u63CF\u8FF0\u4E86\u5229\u7528&quot;\u539F\u5B50&quot;\u4E0E&quot;\u5206\u5B50&quot;\u8FD9\u4E24\u4E2A\u5F62\u5BB9\u8BCD\u6765\u63CF\u8FF0\u77E5\u8BC6\u70B9\uFF0C\u4F1A\u4EA7\u751F\u54EA\u4E9B\u6027\u8D28\uFF0C\u53EA\u8981\u6EE1\u8DB3\u5168\u90E8\u7684\u6027\u8D28\uFF0C\u5219\u8868\u660E\u67D0\u4E2A\u77E5\u8BC6\u70B9\u5BF9\u4E8E\u67D0\u4E2A\u4EBA\u800C\u8A00\u662F\u539F\u5B50\u77E5\u8BC6\u70B9\u3002\u800C\u8FD9\u4E9B\u7279\u6B8A\u7684\u6027\u8D28\uFF0C\u4E5F\u9884\u793A\u7740\u5B83\u6240\u8574\u542B\u7684\u80FD\u529B\u3002\u4E0B\u9762\u4F1A\u7740\u91CD\u9610\u8FF0\u8FD9\u4E9B\u6027\u8D28\u6709\u4EC0\u4E48\u4F5C\u7528\uFF0C\u80FD\u8FBE\u5230\u4EC0\u4E48\u597D\u5904\u3002</p><h1 id="\u63D0\u51FA\u7684\u539F\u56E0" tabindex="-1"><a class="header-anchor" href="#\u63D0\u51FA\u7684\u539F\u56E0" aria-hidden="true">#</a> \u63D0\u51FA\u7684\u539F\u56E0</h1><p>\u4E3A\u4EC0\u4E48\u9700\u8981 <strong>\u539F\u5B50\u77E5\u8BC6</strong> \u8FD9\u4E2A\u6982\u5FF5\u3002\u4E3B\u8981\u662F\u4E3A\u4E86\u8BA9\u4EBA\u4EEC\u66F4\u5BB9\u6613\u8FDB\u884C\u5B66\u4E60\uFF0C\u6253\u7834\u6280\u672F\u4E4B\u95F4\u7684\u58C1\u5792\uFF0C\u8BA9\u4E13\u6709\u540D\u8BCD\u4E0D\u5728\u4E13\u6709\u3002</p><p>\u56E0\u4E3A <strong>\u539F\u5B50\u77E5\u8BC6</strong> \u8FD9\u4E2A\u6982\u5FF5\u4E3B\u8981\u5728\u7684\u8FC7\u7A0B\u5728\u4E8E\u4E0D\u65AD\u7684\u5206\u89E3\u4E00\u4E2A\u9886\u57DF\u6240\u5C55\u73B0\u51FA\u6765\u7684\u5404\u79CD\u6982\u5FF5\uFF0C\u76F4\u5230\u8FD9\u4E2A\u6982\u5FF5\u88AB\u4E2A\u4EBA\u6240\u7406\u89E3\u3002\u8FD9\u6837\u5C31\u662F\u4E00\u4E2A\u4EBA\u7684\u539F\u5B50\u3002</p><h2 id="\u66F4\u5BB9\u6613\u5B66\u4E60" tabindex="-1"><a class="header-anchor" href="#\u66F4\u5BB9\u6613\u5B66\u4E60" aria-hidden="true">#</a> \u66F4\u5BB9\u6613\u5B66\u4E60</h2><p>1.\u8FD9\u662F\u6839\u636E\u65AF\u79D1\u7279-\u6768(Scott H Young)\u7684\u5982\u4F55\u9AD8\u6548\u5B66\u4E60\u7684\u65B9\u6CD5\u91CC\u9762\u6240\u63D0\u51FA\u7684\uFF0C\u5C06\u5404\u79CD\u4E0D\u76F8\u5173\u8054\u7684\u9886\u57DF\u77E5\u8BC6\u8FDB\u884C\u8054\u7CFB\uFF0C\u5F62\u6210\u7C7B\u4F3C\u9AD8\u901F\u516C\u8DEF\u7684\u901A\u9053\uFF0C\u8FD9\u6837\u5C31\u80FD\u901A\u8FC7\u4E00\u4E2A\u9886\u57DF\u7684\u5B66\u4E60\u4E0D\u4EC5\u80FD\u591F\u5B66\u4E60\u8FD9\u4E2A\u9886\u57DF\u7684\u77E5\u8BC6\u70B9\uFF0C\u540C\u65F6\u8FD8\u80FD\u8FDB\u884C\u5176\u4ED6\u9886\u57DF\u7684\u5B66\u4E60\u4EE5\u53CA\u52A0\u6DF1\u5370\u8C61\u3002</p><p>2.\u5982\u679C\u662F\u76F8\u4F3C\u77E5\u8BC6\u70B9\u7684\u4E0D\u540C\u8868\u73B0\uFF0C\u4E5F\u53EF\u4EE5\u8FDB\u884C\u6269\u5C55\u5B66\u8005\u7684\u8BA4\u77E5\uFF1A\u77E5\u8BC6\u70B9\u7684\u4E0D\u540C\u7684\u5E94\u7528\uFF0C\u6216\u8005\u4E0D\u540C\u7684\u5E94\u7528\u9886\u57DF\u3002</p><p>\u4E0A\u9762\u4E24\u70B9\u662F\u5229\u7528\u4E86\u539F\u5B50\u77E5\u8BC6\u70B9\u7684\u53EF\u8FC1\u79FB\u6027\u3002</p><p>3.\u53EF\u4EE5\u5229\u7528\u8FD9\u4E9B\u7EC6\u5C0F\u7684\u539F\u5B50\u8282\u70B9\u8FDB\u884C\u968F\u610F\u7EC4\u5408\uFF0C\u5E76\u5F62\u6210\u65B0\u7684\u77E5\u8BC6\u70B9\uFF08\u6CE8\u610F\u8FD9\u91CC\u7684\u7528\u8BCD\uFF0C\u6CE81\uFF09\uFF0C\u4ECE\u800C\u63A8\u8FDB\u77E5\u8BC6\u7684\u52A0\u901F\u6269\u5C55\u4EE5\u53CA\u5FEB\u901F\u81A8\u80C0\u3002</p><p>\u8FD9\u91CC\u5229\u7528\u4E86\u539F\u5B50\u77E5\u8BC6\u7684\u7EC4\u5408\u6027\u3002</p><p>4.\u800C\u4E14\u80FD\u8BA9\u4EBA\u4ECE\u66F4\u52A0\u5B8F\u89C2\u7684\u89D2\u5EA6\u6765\u67E5\u770B\u81EA\u5DF1\u7684\u6280\u672F\u6808\u4EE5\u53CA\u77E5\u8BC6\u70B9\u5728\u90A3\u4E2A\u8303\u7574\u91CC\u9762\u3002</p><p>5.\u82E5\u80FD\u8FDB\u884C\u5B58\u50A8\u5E76\u5C06\u5176\u53EF\u89C6\u5316\uFF0C\u4E5F\u53EF\u4EE5\u5C06\u6240\u6709\u53EF\u80FD\u7684\u77E5\u8BC6\u4EE5\u56FE\u7684\u5F62\u5F0F\u5C55\u793A\u51FA\u6765\uFF0C\u53EF\u4EE5\u66F4\u52A0\u8BA9\u4EBA\u7C7B\u6211\u4EEC\u77E5\u9053\u6211\u4EEC\u76EE\u524D\u6240\u5904\u7684\u79D1\u6280\u9636\u6BB5\uFF0C\u82E5\u80FD\u8FDB\u884C\u81EA\u884C\u7684\u77E5\u8BC6\u7EC4\u5408\uFF0C\u5219\u4E00\u5B9A\u80FD\u66F4\u52A0\u521B\u9020\u51FA\u591A\u6837\u7684\u9886\u57DF\u3002</p><h1 id="\u613F\u666F" tabindex="-1"><a class="header-anchor" href="#\u613F\u666F" aria-hidden="true">#</a> \u613F\u666F</h1><p>\u6211\u4EEC\u60F3\u8981\u8FDB\u884C\u79C1\u6709\u5E93\u4E0E\u5171\u6709\u5E93\u7684\u642D\u5EFA\u3002</p><p>\u79C1\u6709\u5E93\u8FDB\u884C\u4E2A\u4EBA\u539F\u5B50\u77E5\u8BC6\u7684\u5B58\u50A8</p><p>\u5171\u6709\u5E93\u8FDB\u884C\u5168\u5C40\u77E5\u8BC6\u7684\u5B58\u50A8\u3002</p><p>\u800C\u5BF9\u4E8E\u516C\u5171\u6765\u8BF4\uFF0C\u5E9E\u5927\u7684\u77E5\u8BC6\u91CF\uFF0C\u53EA\u662F\u8FDB\u884C\u8FDE\u63A5\u8FDC\u8FDC\u4E0D\u591F\u7684\uFF0C\u8FD8\u9700\u8981\u8FDB\u884C\u8FC1\u79FB\u3002\u6BD4\u5982\u77E5\u8BC6A\u5728\u9886\u57DFB\u91CC\u9762\u662F\u8FD9\u4E2A\u610F\u601D\uFF0C\u800C\u5728\u9886\u57DFC\u91CC\u9762\u540C\u6837\u6709\u7C7B\u4F3C\u7684\u610F\u601D\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u539F\u5B50\u6BD4\u8F83\u91CD\u8981\uFF0C\u662F\u5426\u53EF\u4EE5\u63A8\u5230\u5728B\u7684\u4EA4\u53C9\u9886\u57DF\u4E2D\u662F\u5426\u6709\u7528\uFF1F</p><p>\u800C\u7EC4\u5408\u66F4\u662F\u524D\u6240\u672A\u6709\u7684\u5DE5\u7A0B\uFF0C\u5F88\u96BE\u60F3\u8C61\u5F88\u591A\u77E5\u8BC6\u7EC4\u5408\u5728\u4E00\u8D77\u662F\u5426\u80FD\u76F4\u63A5\u5FEB\u901F\u7684\u89E3\u7B54\u6211\u4EEC\u7684\u73B0\u5728\u6240\u9047\u5230\u7684\u5B66\u754C\u4E0A\u7684\u5404\u79CD\u95EE\u9898\uFF1F\u8BF4\u4E0D\u5B9A\u7269\u7406\u5B66\u4E0A\u7684\u7A81\u7834\u53EF\u80FD\u9700\u8981\u7ECF\u6D4E\u5B66\u7684\u67D0\u79CD\u6548\u5E94\uFF1F\u6216\u8005\u53CD\u8FC7\u6765\uFF1F</p><h3 id="\u6DFB\u52A0\u529F\u80FD\u6216\u4FEE\u590D\u9519\u8BEF" tabindex="-1"><a class="header-anchor" href="#\u6DFB\u52A0\u529F\u80FD\u6216\u4FEE\u590D\u9519\u8BEF" aria-hidden="true">#</a> \u6DFB\u52A0\u529F\u80FD\u6216\u4FEE\u590D\u9519\u8BEF</h3><p>\u6DFB\u52A0\u529F\u80FD\u6216\u4FEE\u590D\u9519\u8BEF \u5982\u679C\u60A8\u62E5\u6709\u8F6F\u4EF6\u5DE5\u7A0B\u6280\u80FD\uFF0C\u5219\u53EF\u4EE5\u901A\u8FC7\u7F16\u8F91\u5176\u6E90\u4EE3\u7801\u4E3A\u9879\u76EE\u505A\u51FA\u8D21\u732E\u3002\u6709\u5173\u8FD9\u65B9\u9762\u7684\u6307\u5357\uFF0C\u8BF7\u53C2\u9605<a href="https://github.com/misskey-dev/misskey/blob/develop/CONTRIBUTING.md" target="_blank" rel="noopener noreferrer">\u8FD9\u91CC</a>.</p><h3 id="\u53C2\u4E0E\u8BA8\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u53C2\u4E0E\u8BA8\u8BBA" aria-hidden="true">#</a> \u53C2\u4E0E\u8BA8\u8BBA</h3><p>\u4F60\u53EF\u4EE5\u901A\u8FC7\u5206\u4EAB\u4F60\u5BF9\u65B0\u529F\u80FD\u6216\u73B0\u6709\u529F\u80FD\u7684\u610F\u89C1\u4EE5\u53CA\u62A5\u544A\u9519\u8BEF\u6765\u505A\u51FA\u8D21\u732E\u3002\u6B64\u7C7B\u8BA8\u8BBA\u53EF\u4EE5\u5728<a href="https://github.com/misskey-dev" target="_blank" rel="noopener noreferrer">GitHub</a>\u6216<a href="https://forum.misskey.io/" target="_blank" rel="noopener noreferrer">\u8BBA\u575B</a>\u7B49\u4E0A\u8FDB\u884C\u3002</p><h3 id="\u7FFB\u8BD1\u6587\u672C" tabindex="-1"><a class="header-anchor" href="#\u7FFB\u8BD1\u6587\u672C" aria-hidden="true">#</a> \u7FFB\u8BD1\u6587\u672C</h3><p>Ocean Blog \u6709\u591A\u79CD\u8BED\u8A00\u7248\u672C\uFF08\u4E5F\u79F0\u4E3A i18n\uFF0C\u662F\u56FD\u9645\u5316\u7684\u7F29\u5199\uFF09\u3002\u867D\u7136\u539F\u59CB\u8BED\u8A00\u901A\u5E38\u662F\u4E2D\u6587\u7B80\u4F53\uFF0C\u4F46\u5FD7\u613F\u8005\u6B63\u5728\u5C06Ocean Blog\u7FFB\u8BD1\u6210\u5176\u4ED6\u8BED\u8A00\u3002\u5E2E\u52A9\u8FD9\u9879\u5DE5\u4F5C\u4E5F\u662F\u4E00\u79CD\u8D21\u732E\u5F62\u5F0F\u3002Ocean Blog\u4F7F\u7528\u540D\u4E3A<a href="https://crowdin.com/project/misskey" target="_blank" rel="noopener noreferrer">Crowdin\u6765\u7BA1\u7406\u5176\u7FFB\u8BD1\u7684\u670D\u52A1\u3002</a></p><h3 id="sharing-your-impressions" tabindex="-1"><a class="header-anchor" href="#sharing-your-impressions" aria-hidden="true">#</a> Sharing your impressions</h3><p>Besides reporting bugs, please also feel free to share positive impressions such as which parts of Misskey you personally like, or things about Misskey that you find fun.As things like these serve as motivation for the developers, it also counts as indirect support for the project.</p><h3 id="increasing-the-number-of-misskists" tabindex="-1"><a class="header-anchor" href="#increasing-the-number-of-misskists" aria-hidden="true">#</a> Increasing the number of Misskists</h3><p>&quot;Misskist&quot; refers to the people using Misskey. By spreading the word about Misskey through introducing it to your acquaintances, the number of Misskists may increase, which serves as motivation for the developers.</p><h3 id="making-donations" tabindex="-1"><a class="header-anchor" href="#making-donations" aria-hidden="true">#</a> Making donations</h3>',60),p=t('<h2 id="frequently-asked-questions" tabindex="-1"><a class="header-anchor" href="#frequently-asked-questions" aria-hidden="true">#</a> Frequently Asked Questions</h2><h3 id="what-is-this-project-aiming-to-achieve" tabindex="-1"><a class="header-anchor" href="#what-is-this-project-aiming-to-achieve" aria-hidden="true">#</a> What is this project aiming to achieve?</h3><p>To put it bluntly, while this is a bit vague, Misskey is aiming to be a widely-used all-purpose platform. Unlike other platforms, Misskey is not grounded in a specific idea (for example, anti-centralization) or vision and being developed with that in mind, so it&#39;s a bit &quot;aimless&quot; in this respect. On the other hand though, this creates a flexibility due to not being bound by a specific direction to go towards.</p><h3 id="is-misskey-being-developed-by-a-company" tabindex="-1"><a class="header-anchor" href="#is-misskey-being-developed-by-a-company" aria-hidden="true">#</a> Is Misskey being developed by a company?</h3><p>No.Misskey is being developed by an individual person and not commercialized through connection with any particular company. Development members are generally volunteers. Additionally, while there are some corporate sponsors, development is still centered around the community.</p><h3 id="who-is-managing-misskey" tabindex="-1"><a class="header-anchor" href="#who-is-managing-misskey" aria-hidden="true">#</a> Who is managing Misskey?</h3><p>Due to the distributed nature of Misskey, each individual server has their own administration.Therefore, not all of Misskey is managed by one individual person or company. This means that, as the development team does not control individual servers, for management-related questions you should contact your server&#39;s administration instead. You can verify who is managing your individual server on <a href="/about">this page</a>. If you were to create a server, then you would also become its administrator.</p><h3 id="which-server-should-i-choose" tabindex="-1"><a class="header-anchor" href="#which-server-should-i-choose" aria-hidden="true">#</a> Which server should I choose?</h3>',8),u=a("p",null,"Generally speaking, no matter which server you join, you will still be able to connect with users from all other servers.",-1),f=a("h3",{id:"how-can-i-create-my-own-server",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#how-can-i-create-my-own-server","aria-hidden":"true"},"#"),e(" How can I create my own server?")],-1),m=t('<h3 id="what-kind-of-technology-does-misskey-use" tabindex="-1"><a class="header-anchor" href="#what-kind-of-technology-does-misskey-use" aria-hidden="true">#</a> What kind of technology does Misskey use?</h3><p>As development on Misskey progressed, the technology it has used has changed greatly.In the beginning, it used a combination of MySQL + PHP + jQuery, but currently uses the following.</p><ul><li>Server-side: Node.js</li><li>Database: PostgreSQL, Redis</li><li>UI Framework: Vue.js</li><li>Programming language: TypeScript</li></ul><p>In addition to this, from Misskey derived technology such as MFM or AiScript are also being used.</p><h3 id="is-this-a-fork-of-mastodon" tabindex="-1"><a class="header-anchor" href="#is-this-a-fork-of-mastodon" aria-hidden="true">#</a> Is this a fork of Mastodon?</h3><p>No.Misskey is a project completely different from Mastodon or other alike projects. It has been in development for a long time already.However, it only became a distributed network after the appearance of Mastodon. Besides both projects implementing the ActivityPub protocol, there is no relation between the two.</p><h3 id="are-there-any-apps-for-ios-android-available" tabindex="-1"><a class="header-anchor" href="#are-there-any-apps-for-ios-android-available" aria-hidden="true">#</a> Are there any apps for iOS / Android available?</h3><p>While no official Misskey app for either OS exists, there are several third-party applications. For details, please check <a href="./apps">here</a>.</p><p>However, functionality of third-party applications will inevitably lag behind the official Web client, so unless you really want to use a native application, we recommend the official Web client instead. As the Misskey Web client supports PWA, it is also possible to make it act as if it was a native application instead. For details regarding this, please check <a href="todo">here</a>.</p><h3 id="where-can-i-download-misskey-s-logo-or-icon" tabindex="-1"><a class="header-anchor" href="#where-can-i-download-misskey-s-logo-or-icon" aria-hidden="true">#</a> Where can I download Misskey&#39;s logo or icon?</h3>',10),g=a("h3",{id:"who-s-that-cute-cat-eared-girl-that-you-see-sometimes",tabindex:"-1"},[a("a",{class:"header-anchor",href:"#who-s-that-cute-cat-eared-girl-that-you-see-sometimes","aria-hidden":"true"},"#"),e(" Who's that cute cat-eared girl that you see sometimes?")],-1),y=a("p",null,"It's Misskey's guardian deity, Ai.(They called Ai cute, yay!)",-1),b=a("div",{class:"info"},[e("\u2139\uFE0F To read more about Ai, check "),a("a",{href:"https://xn--931a.moe/",target:"_blank"},"here"),e(" (Japanese-only).")],-1);function k(v,w){const i=n("RouterLink");return h(),d("div",null,[c,a("p",null,[s(i,{to:"/zh/docs/donate.html"},{default:r(()=>[e("Please take a look here")]),_:1})]),p,a("p",null,[s(i,{to:"/zh/instances.html"},{default:r(()=>[e("You can find an (incomplete) list of servers here.")]),_:1}),e(" Depending on the server, its community or central theme (for example, liking a show) may vary, so if there is a server that suits your interests, joining it would likely be a good choice. Besides that, server size, userbase, country or spoken language, reliance or trust in the administration team and many other things can also serve as criteria. There is however no single server that serves as the official server of Misskey.You also have the choice of creating a new server of your own.")]),u,f,a("p",null,[e("Thank you for having an interest in creating a Misskey server. In the current year of 2022 there is no specialized Misskey hosting service, so creating a new server requires a certain amount of knowledge. Please see "),s(i,{to:"/zh/docs/install.html"},{default:r(()=>[e("here")]),_:1}),e(" for more information regarding this.")]),m,a("p",null,[e("Please refer to the "),s(i,{to:"/zh/appendix/assets.html"},{default:r(()=>[e("assets collection")]),_:1}),e(".")]),g,y,b])}var _=o(l,[["render",k],["__file","atomic-knowledge.html.vue"]]);export{_ as default};
